import React, { useState } from "react";

const Home = () => {
  const [task, setTask] = useState();
  return (
    <>
      <main className="form">
        <section>
          <h2>Task Manager</h2>
          <form className="form-row">
            <input
              type="text"
              className="form-input"
              placeholder="e.g debug code"
              value={task}
              onChange={(e) => setTask(e.target.value)}
            />
            <button  type="submit">submit</button>
          </form>
        </section>
      </main>
    </>
  );
};

export default Home;
