import React, { useContext, useEffect, useState } from "react";
import axios from 'axios'
import { AppContext } from "../contexts/appContext";
import { Alert } from "../components";

const Home = () => {
  const { fetchTasks, userTasks, createTask, displayAlert } =
    useContext(AppContext); 
  const [task, setTask] = useState(''); 



  useEffect(() => {
    fetchTasks()
  }, [])

  const handleSubmit = (e) => {
    e.preventDefault()
    if (!task) {
      displayAlert()
      return;
    }
    return createTask(task) 
  }


  return (
    <>
      <main className="container-task">
        <section className="form">
          <h2>Task Manager</h2>

          <form className="form-row">
            <input
              type="text"
              className="task-form-input"
              placeholder="e.g debug code"
              value={task}
              onChange={(e) => setTask(e.target.value)}
            />
            <button className="submit-task-btn" type="submit" onClick={handleSubmit}>
              submit
            </button>
          </form>
        </section>

        <section >
          {userTasks.length <= 0 ? <div className="form"><h3>No Tasks</h3></div> : userTasks.map((task, i) => {
            return (
              <div className="form" key={i}>
                {task.name}
              </div>
            );
          })}
        </section>
      </main>    
    </>
  );
};

export default Home;
