      const formattedDate = currentDate.toLocaleDateString("en-US", {
        year: "numeric",
        month: "long",
        day: "numeric",
      });


  return (
    <>
      <nav className='navbar'>
              <p>Today:  {formattedDate}</p> 
              <button className='logout-btn'>log out</button>
      </nav>
    </>
  );
}

export default Navbar
