import React, { useContext, useEffect, useState } from "react";
import axios from 'axios'
import { AppContext } from "../contexts/appContext";

const Home = () => {
  const {token} = useContext(AppContext) 
  const [allTasks, setAllTasks] = useState([]);

  const authFetch = axios.create({
    baseURL: '/api/v1',
    headers: {
      Authorization : `Bearer ${token}`
    }
  })

  const fetchTasks = async () => {
    try {
      const response = await authFetch.get('/tasks') 
      const data = response.data
     setAllTasks(data.tasks) 
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    fetchTasks()
  },[])


  return (
    <>
      <main className="container-task">
        <section className="form">
          <h2>Task Manager</h2>
          <form className="form-row">
            <input
              type="text"
              className="task-form-input"
              placeholder="e.g debug code"
              value={task}
              onChange={(e) => setTask(e.target.value)}
            />
            <button className="submit-task-btn" type="submit">
              submit
            </button>
          </form>
        </section>

        <section >
          {allTasks.length <= 0 ? <div className="form"><h3>No Tasks</h3></div> : allTasks.map((task, i) => {
            return (
              <div className="form" key={i}>
                {task.name}
              </div>
            );
          })}
        </section>
      </main>
    </>
  );
};

export default Home;
