import React from "react";
import { useReducer } from "react";
import { createContext } from "react";
import reducer from "./reducer";
import axios from "axios";


import {
  SHOW_ALERT,
  CLEAR_ALERT,
  REGISTER_USER_BEGIN,
  REGISTER_USER_SUCCESS,
  REGISTER_USER_ERROR,
  LOGIN_USER_BEGIN,
  LOGIN_USER_SUCCESS,
  LOGIN_USER_ERROR,
  FETCH_USER_TASKS_BEGIN,
  FETCH_USER_TASKS_SUCCESS,
  FETCH_USER_TASKS_ERROR
} from "./actions";



const user = localStorage.getItem("user");
const token = localStorage.getItem("token");

export const AppContext = createContext();

export const initialState = {
  isLoading: false,
  showAlert: false,
  alertType: "",
  alertText: "",
  isLoading: false,
  user: user ? JSON.parse(user) : null,
  token: token,
  userTasks : []
};

const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const displayAlert = () => {
    dispatch({ type: SHOW_ALERT });
    clearAlert();
  };

  const clearAlert = () => {
    setTimeout(() => {
      dispatch({ type: CLEAR_ALERT });
    },3000)
  };

  const addToLocalStorage = ({ user, token }) => {
    localStorage.setItem("user", JSON.stringify(user));
    localStorage.setItem("token", token);
  };
  const removeFromLocalStorage = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
  }; 

  const registerUser = async (currentUser) => {
    dispatch({ type: REGISTER_USER_BEGIN });  
    try {
      const response = await axios.post("/api/v1/auth/register", currentUser);
      const { user, token } = response.data;
      dispatch({ type: REGISTER_USER_SUCCESS, payload: { user, token } });
      addToLocalStorage({ user, token });
    } catch (error) {
      dispatch({
        type: REGISTER_USER_ERROR,
        payload: { msg: error.response.data.msg },
      });
    }
   clearAlert();
  };

  const loginUser = async (currentUser) => {
    dispatch({ type: LOGIN_USER_BEGIN });
    try {
      const response = await axios.post("/api/v1/auth/login", currentUser);
      const { token, user } = response.data;
      dispatch({ type: LOGIN_USER_SUCCESS, payload: { user, token } });
      addToLocalStorage({ user, token });
    } catch (error) {
      dispatch({
        type: LOGIN_USER_ERROR,
        payload: { msg: error.response.data.msg },
      });
    }
    clearAlert();
  };


const authFetch = axios.create({
  baseURL: "/api/v1",
});

  authFetch.interceptors.request.use( function (config) {
  (config) => {
    config.headers.common['Authorization'] = `Bearer ${token}`
    return config
  },
    (error) => {
      return Promise.reject(error)
    }
  })

  authFetch.interceptors.response.use((response) => {
    return response
  },
    (error) => {
      console.log(error.response);
      if (error.response.status === 401) {
        console.log('AUTH ERROR');
      }
      return Promise.reject(error)
    }
  )







  const fetchTasks = async () => {
  dispatch({type: FETCH_USER_TASKS_BEGIN})
  try {
    const response = await authFetch.get("/tasks");
    const data = response.data;
   dispatch({type: FETCH_USER_TASKS_SUCCESS, payload: data.tasks})
  } catch (error) {
    dispatch({type: FETCH_USER_TASKS_SUCCESS, })
    console.log(error);
  }
};








  return (
    <AppContext.Provider
      value={{
        ...state,
        displayAlert,
        clearAlert,
        removeFromLocalStorage,
        registerUser,
        loginUser,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};

export default AppProvider;
