import React from 'react'
import SHOW_ALERT from './actions'
import { initialState } from './appContext'

initialState
const reducer = (state, action) => {
    if (action.type === SHOW_ALERT) {
        return ({ ...state, showAlert:!showAlert }
        )
    }
}
export default reducer
